namespace xbytechat.api.AuthModule.Models
{
    public class User
    {
        public Guid Id { get; set; }

        // üîó FK to Business
        public Guid? BusinessId { get; set; }
        public Business Business { get; set; }

        // üë§ User Info
        [Required]
        public string Name { get; set; }

        [Required]
        [EmailAddress]
        public string Email { get; set; }

        [Required]
        public string PasswordHash { get; set; }

        // üõ°Ô∏è Role System
        // üõ°Ô∏è Role System (FK + Navigation)
        public Guid? RoleId { get; set; }
        public Role Role { get; set; }// admin / business / agent / staff

        // ‚úÖ Status Management
        public string Status { get; set; } = "Pending"; // Active / Hold / Rejected / Pending

        public DateTime CreatedAt { get; set; } = DateTime.UtcNow;

        // üóëÔ∏è Soft Delete Role 
        public bool IsDeleted { get; set; } = false;
        public DateTime? DeletedAt { get; set; }

        public List<CampaignSendLog> SendLogs { get; set; }
        public ICollection<MessageStatusLog> MessageStatusLogs { get; set; }

        // üÜï Permission Navigation
        public ICollection<UserPermission> UserPermissions { get; set; } // üí° Enables .WithMany(u => u.UserPermissions)
        public string? RefreshToken { get; set; }
        public DateTime? RefreshTokenExpiry { get; set; }

    }
}

