        public async Task<ResponseResult> LoginAsync(UserLoginDto dto)
        {
            _logger.LogInformation("üîë Login attempt for email: {Email}", dto.Email);
            var hashedPassword = HashPassword(dto.Password);

            var user = await _userRepo
                .AsQueryable()
                .Where(u => u.Email == dto.Email && u.PasswordHash == hashedPassword && !u.IsDeleted)
                .Include(u => u.Role)
                .FirstOrDefaultAsync();

            if (user == null)
            {
                _logger.LogWarning("‚ùå Login failed: Invalid email or password for {Email}", dto.Email);
                return ResponseResult.ErrorInfo("‚ùå Invalid email or password");
            }

            var roleName = user.Role?.Name?.Trim().ToLower() ?? "unknown";
            var isAdminType = roleName is "admin" or "superadmin" or "partner" or "reseller";

            // ‚úÖ NEW: Block deactivated/non-active users (important for TeamStaff)
            if (!isAdminType)
            {
                var st = (user.Status ?? "").Trim().ToLowerInvariant();

                // You can add/remove statuses here as per your system rules
                if (st is "hold" or "inactive" or "rejected" or "pending")
                {
                    _logger.LogWarning("üö´ Login blocked due to user status. Email={Email}, Status={Status}", dto.Email, user.Status);
                    return ResponseResult.ErrorInfo("‚ùå Your account is not active. Please contact your administrator.");
                }
            }

            if (user.BusinessId == null && !isAdminType)
            {
                _logger.LogWarning("‚ùå Login denied for {Email}: No BusinessId and not admin", dto.Email);
                return ResponseResult.ErrorInfo("‚ùå Your account approval is pending. Please contact your administrator or support.");
            }

            Business? business = null;

            // This is the REAL plan id from DB (can be internal)
            Guid? planId = null;

            // This is what we expose to the FRONTEND (hide internal plans)
            Guid? exposedPlanId = null;

            string companyName = string.Empty;
            string businessId = user.BusinessId?.ToString() ?? string.Empty;

            if (user.BusinessId != null)
            {
                business = await _businessService.Query()
                    .Include(b => b.Plan) // ensure Plan loaded so we can check IsInternal
                    .FirstOrDefaultAsync(b => b.Id == user.BusinessId.Value);

                if (business == null)
                    return ResponseResult.ErrorInfo("‚ùå Associated business not found.");

                if (business.Status == Business.StatusType.Pending)
                    return ResponseResult.ErrorInfo("‚è≥ Your business is under review. Please wait for approval.");

                planId = business.PlanId;
                companyName = business.CompanyName ?? string.Empty;

                // üß† Decide whether to expose plan to frontend
                var isInternalPlan = business.Plan?.IsInternal == true;
                exposedPlanId = isInternalPlan ? null : planId;

                _logger.LogInformation(
                    "Business {BusinessId} login. Status: {Status}, DbPlanId: {DbPlanId}, ExposedPlanId: {ExposedPlanId}, IsInternal: {IsInternal}",
                    business.Id, business.Status, planId, exposedPlanId, isInternalPlan
                );
            }

            if (isAdminType)
            {
                // Admins don‚Äôt get plan restrictions and shouldn't show a plan in UI
                companyName = "xByte Admin";
                businessId = string.Empty;
                planId = null;
                exposedPlanId = null;
            }

            // üî• Compute EFFECTIVE permissions (plan ‚à© role) and derive features
            var (permCodes, featureKeys) = isAdminType
                ? (await GetAllActivePermissions(), new List<string> { "Dashboard", "Messaging", "CRM", "Campaigns", "Catalog", "AdminPanel" })
                : await GetEffectivePermissionsAndFeaturesAsync(user.Id);

            // üé´ Generate JWT ‚Üí use EXPOSED plan id (hide internal/system plans)
            var token = _jwtTokenService.GenerateToken(
                userId: user.Id.ToString(),
                role: roleName,
                userName: user.Name ?? string.Empty,
                email: user.Email ?? string.Empty,
                status: user.Status ?? "unknown",
                businessId: businessId,
                companyName: companyName,
                permissions: permCodes ?? new List<string>(),
                planId: exposedPlanId?.ToString() ?? string.Empty,
                features: featureKeys,
                hasAllAccess: isAdminType
            );

            try
            {
                var jwt = new JwtSecurityTokenHandler().ReadJwtToken(token);
                var pid = jwt.Claims.FirstOrDefault(c => c.Type == "plan_id")?.Value;
                _logger.LogInformation("üîé JWT includes plan_id (exposed): {PlanId}", pid ?? "<null>");
            }
            catch
            {
                // ignore
            }

            var userDto = new UserDto
            {
                Id = user.Id,
                Name = user.Name,
                Email = user.Email,
                Role = roleName,
                Status = user.Status,
                CreatedAt = user.CreatedAt,
                BusinessId = string.IsNullOrEmpty(businessId) ? Guid.Empty : Guid.Parse(businessId),
                CompanyName = companyName,

                // üëá important: return ONLY the exposed plan id to the frontend
                PlanId = exposedPlanId,

                AccessToken = null
            };

            _logger.LogInformation(
                "‚úÖ Login successful for {Email}, Role: {Role}, DbPlanId: {DbPlanId}, ExposedPlanId: {ExposedPlanId}",
                dto.Email, roleName, planId, exposedPlanId
            );

            return new ResponseResult
            {
                Success = true,
                Message = "‚úÖ Login successful",
                Data = userDto,
                Token = token
            };
        }

        public async Task<ResponseResult> SignupAsync(SignupBusinessDto dto)
        {
            _logger.LogInformation("üü¢ Signup attempt: {Email}", dto.Email);
            var result = await _businessService.SignupBusinessAsync(dto);

            if (!result.Success)
            {
                _logger.LogWarning("‚ùå Signup failed for {Email}: {Msg}", dto.Email, result.Message);
                return ResponseResult.ErrorInfo(result.Message);
            }

            var business = await _businessService.GetBusinessByEmailAsync(dto.Email);

            if (business == null)
            {
                _logger.LogError("‚ùå Signup succeeded but business retrieval failed for {Email}", dto.Email);
                return ResponseResult.ErrorInfo("‚ùå Signup succeeded but business retrieval failed.");
            }

            // No extra UpdateBusinessAsync call here.
            // CreatedByPartnerId is set inside SignupBusinessAsync.

            _logger.LogInformation("‚úÖ Signup successful for {Email} (BusinessId: {BusinessId})", dto.Email, business.Id);
            return ResponseResult.SuccessInfo("‚úÖ Signup successful. Pending approval.", new { BusinessId = business.Id });
        }

        public async Task<ResponseResult> RefreshTokenAsync(string refreshToken)
        {
            _logger.LogInformation("üîÑ RefreshToken attempt");

            var user = await _userRepo
                .AsQueryable()
                .Include(u => u.Role)
                .Include(u => u.Business)
                    .ThenInclude(b => b.BusinessPlanInfo)
                .Include(u => u.Business)
                    .ThenInclude(b => b.Plan) // üëà ensure Plan is loaded
                .FirstOrDefaultAsync(u =>
                    u.RefreshToken == refreshToken &&
                    u.RefreshTokenExpiry > DateTime.UtcNow
                );

            if (user == null)
            {
                _logger.LogWarning("‚ùå Invalid or expired refresh token.");
                return ResponseResult.ErrorInfo("‚ùå Invalid or expired refresh token.");
            }

            var roleName = user.Role?.Name?.Trim().ToLower() ?? "unknown";
            var isAdminType = roleName is "admin" or "superadmin" or "partner" or "reseller";

            // ‚úÖ NEW: Block refresh for non-active users (prevents silent re-login for disabled staff)
            if (!isAdminType)
            {
                var st = (user.Status ?? "").Trim().ToLowerInvariant();
                if (st is "hold" or "inactive" or "rejected" or "pending")
                {
                    _logger.LogWarning("üö´ RefreshToken blocked due to user status. UserId={UserId}, Email={Email}, Status={Status}",
                        user.Id, user.Email, user.Status);

                    return ResponseResult.ErrorInfo("‚ùå Your account is not active. Please contact your administrator.");
                }
            }

            string companyName;
            string businessId;

            // REAL plan from DB
            Guid? dbPlanId = user.Business?.PlanId;

            // What we expose to frontend (null for internal plans)
            string? exposedPlanIdForClaim = null;

            if (isAdminType)
            {
                companyName = "xByte Admin";
                businessId = string.Empty;
                dbPlanId = null;
                exposedPlanIdForClaim = null;
            }
            else
            {
                companyName = user.Business?.CompanyName ?? string.Empty;
                businessId = user.BusinessId?.ToString() ?? string.Empty;

                if (dbPlanId.HasValue)
                {
                    var isInternalPlan = user.Business?.Plan?.IsInternal == true;
                    if (!isInternalPlan)
                    {
                        exposedPlanIdForClaim = dbPlanId.Value.ToString();
                    }

                    _logger.LogInformation(
                        "üîÑ RefreshToken: Business {BusinessId}, DbPlanId: {DbPlanId}, ExposedPlanId: {ExposedPlanId}, IsInternal: {IsInternal}",
                        businessId, dbPlanId, exposedPlanIdForClaim, isInternalPlan
                    );
                }
            }

            var (permCodes, featureKeys) = isAdminType
                ? (await GetAllActivePermissions(), new List<string> { "Dashboard", "Messaging", "CRM", "Campaigns", "Catalog", "AdminPanel" })
                : await GetEffectivePermissionsAndFeaturesAsync(user.Id);

            var claims = new List<Claim>
    {
        new Claim(JwtRegisteredClaimNames.Sub, user.Id.ToString()),
        new Claim("id", user.Id.ToString()),
        new Claim(ClaimTypes.NameIdentifier, user.Id.ToString()),
        new Claim("email", user.Email ?? ""),
        new Claim("name", user.Name ?? ""),
        new Claim("status", user.Status ?? "unknown"),
        new Claim("businessId", businessId),
        new Claim("companyName", companyName),
        new Claim("permissions", string.Join(",", permCodes ?? new List<string>())),
        new Claim("features", string.Join(",", featureKeys ?? new List<string>())),
        new Claim("hasAllAccess", isAdminType ? "true" : "false"),
        new Claim("role", roleName),
        new Claim(ClaimTypes.Role, roleName)
    };

            // üëâ Only add plan_id claim if we want the frontend to see it
            if (!string.IsNullOrWhiteSpace(exposedPlanIdForClaim))
            {
                claims.Add(new Claim("plan_id", exposedPlanIdForClaim));
            }

            var token = _jwtTokenService.GenerateToken(claims);

            // üîÅ Rotate refresh token
            var newRefreshToken = Guid.NewGuid().ToString("N");
            user.RefreshToken = newRefreshToken;
            user.RefreshTokenExpiry = DateTime.UtcNow.AddDays(30);
            _userRepo.Update(user);

            _logger.LogInformation(
                "üîÑ Token refreshed for user {UserId}, role {Role}, DbPlanId: {DbPlanId}, ExposedPlanId: {ExposedPlanId}",
                user.Id, roleName, dbPlanId, exposedPlanIdForClaim
            );

            return ResponseResult.SuccessInfo("üîÑ Token refreshed", new
            {
                accessToken = token,
                refreshToken = newRefreshToken
            });
        }




