using xbytechat.api.Features.TemplateModule.Abstractions;
using xbytechat.api.Features.TemplateModule.Config;

namespace xbytechat.api.Features.TemplateModule.Services;

public sealed class MetaUploadService : IMetaUploadService
{
    private readonly IHttpClientFactory _httpFactory;
    private readonly IMetaCredentialsResolver _creds;
    private readonly UploadLimitsOptions _opts;
    private readonly ILogger<MetaUploadService> _logger;

    public MetaUploadService(
        IHttpClientFactory httpFactory,
        IMetaCredentialsResolver creds,
        IOptions<UploadLimitsOptions> opts,
        ILogger<MetaUploadService> logger)
    {
        _httpFactory = httpFactory;
        _creds = creds;
        _opts = opts.Value;
        _logger = logger;
    }

    public async Task<HeaderUploadResult> UploadHeaderAsync(
        Guid businessId,
        HeaderMediaType mediaType,
        Stream? fileStream,
        string? fileName,
        string? sourceUrl,
        CancellationToken ct = default)
    {
        if ((fileStream is null && string.IsNullOrWhiteSpace(sourceUrl)) ||
            (fileStream is not null && !string.IsNullOrWhiteSpace(sourceUrl)))
            throw new ArgumentException("Provide either a file or a sourceUrl, not both.");

        // If sourceUrl provided, download then proceed
        if (fileStream is null && sourceUrl is not null)
        {
            using var http = new HttpClient();
            using var resp = await http.GetAsync(sourceUrl, HttpCompletionOption.ResponseHeadersRead, ct);
            resp.EnsureSuccessStatusCode();

            var tmp = new MemoryStream();
            await resp.Content.CopyToAsync(tmp, ct);
            tmp.Position = 0;

            var mime = resp.Content.Headers.ContentType?.MediaType ?? "application/octet-stream";
            var fname = Path.GetFileName(new Uri(sourceUrl).LocalPath);
            return await UploadCoreAsync(businessId, mediaType, tmp, fname, mime, ct);
        }

        // File provided
        if (fileStream is not null)
        {
            var mime = GuessMime(fileName, mediaType);
            return await UploadCoreAsync(businessId, mediaType, fileStream, fileName ?? "upload.bin", mime, ct);
        }

        throw new InvalidOperationException("No file or URL provided.");
    }

    private async Task<HeaderUploadResult> UploadCoreAsync(
        Guid businessId,
        HeaderMediaType mediaType,
        Stream content,
        string fileName,
        string mime,
        CancellationToken ct)
    {
        // Ensure we can read length + seek (so we can retry Path B after Path A)
        if (!content.CanSeek)
        {
            var mem = new MemoryStream();
            await content.CopyToAsync(mem, ct);
            mem.Position = 0;
            content = mem;
        }

        var size = content.Length;

        // Size/MIME guards
        switch (mediaType)
        {
            case HeaderMediaType.IMAGE:
                if (size > _opts.ImageMaxBytes) throw new InvalidOperationException("Image too large.");
                if (!_opts.AllowedImageMime.Contains(mime, StringComparer.OrdinalIgnoreCase))
                    throw new InvalidOperationException($"Invalid image MIME: {mime}");
                break;

            case HeaderMediaType.VIDEO:
                if (size > _opts.VideoMaxBytes) throw new InvalidOperationException("Video too large.");
                if (!_opts.AllowedVideoMime.Contains(mime, StringComparer.OrdinalIgnoreCase))
                    throw new InvalidOperationException($"Invalid video MIME: {mime}");
                break;

            case HeaderMediaType.DOCUMENT:
                if (size > _opts.DocumentMaxBytes) throw new InvalidOperationException("Document too large.");
                if (!_opts.AllowedDocMime.Contains(mime, StringComparer.OrdinalIgnoreCase))
                    throw new InvalidOperationException($"Invalid document MIME: {mime}");
                break;
        }

        // ‚îÄ‚îÄ STUB MODE ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ
        if (_opts.UseStubHandle)
        {
            var handleStub = $"{(int)mediaType}:{Guid.NewGuid():N}".Insert(1, "::");
            return new HeaderUploadResult(handleStub, mime, size, IsStub: true);
        }

        // ‚îÄ‚îÄ REAL MODE: Meta Resumable Upload ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ
        var c = await _creds.ResolveAsync(businessId, ct);

        var baseRoot = (c.GraphBaseUrl ?? "").TrimEnd('/');
        var versionPart = string.IsNullOrWhiteSpace(c.GraphVersion) ? "" : "/" + c.GraphVersion.Trim('/');
        var baseWithVersion = $"{baseRoot}{versionPart}";

        using var client = _httpFactory.CreateClient("meta-graph");
        client.BaseAddress = new Uri(baseWithVersion + "/");
        client.DefaultRequestHeaders.Authorization = new AuthenticationHeaderValue("Bearer", c.AccessToken);

        // Meta expects IMAGE|VIDEO|DOCUMENT
        // Meta expects the MIME type (e.g. "image/jpeg"), NOT just "IMAGE".
        string fileTypeParam = mime;

        _logger.LogInformation("Starting Meta Upload. WABA: {WabaId}, Type: {Type}, Mime: {Mime}, Size: {Size}", 
             c.WabaId, mediaType, mime, size);


        // Path C: Classic (Numeric ID) - PROMOTED to Primary for Template compatibility
        // Returns a numeric Media ID (e.g. "12345...") which is strictly required by the Cloud API for template headers.
        try 
        {
             var targetId = !string.IsNullOrWhiteSpace(c.PhoneNumberId) ? c.PhoneNumberId : c.WabaId;
             _logger.LogInformation("MetaUpload: Attempting Path C (Classic) for TargetId={TargetId}...", targetId);
             var handleC = await TryPathC_ClassicMediaEndpointAsync(client, targetId!, new NonDisposableStream(content), fileName, mime, size, ct);
             if (!string.IsNullOrWhiteSpace(handleC))
             {
                _logger.LogInformation("MetaUpload: Path C Success. MediaId={MediaId}", handleC);
                return new HeaderUploadResult(handleC!, mime, size, false);
             }
        }
        catch (Exception ex)
        {
             _logger.LogWarning(ex, "MetaUpload: Path C (Classic) failed. Falling back to Path A/B.");
        }

        // Rewind
        try { content.Position = 0; } catch { }

        // Path A: uploads session (Fallback for larger files)
        // Returns a handle (4::...) OR a resolved numeric ID if possible.
        var handleA = await TryPathA_UploadsEndpointAsync(client, c.WabaId, fileTypeParam, new NonDisposableStream(content), fileName, mime, size, ct);
        if (!string.IsNullOrWhiteSpace(handleA))
        {
            _logger.LogInformation("MetaUpload: Path A Success. Handle={Handle}. Attempting resolution...", handleA);
            var resolvedId = await ResolveMediaIdAsync(client, handleA!, null, ct);
            _logger.LogInformation("MetaUpload: Path A Final Result={Result}", resolvedId);
            return new HeaderUploadResult(resolvedId, mime, size, false);
        }

        // Rewind
        try { content.Position = 0; } catch { }

        // Path B: Phased (Fallback)
        var handleB = await TryPathB_PhasedUploadAsync(client, c.WabaId, fileTypeParam, new NonDisposableStream(content), fileName, mime, size, ct);
        if (!string.IsNullOrWhiteSpace(handleB))
        {
             _logger.LogInformation("MetaUpload: Path B Success. Handle={Handle}. Attempting resolution...", handleB);
             var resolvedId = await ResolveMediaIdAsync(client, handleB!, null, ct);
             return new HeaderUploadResult(resolvedId, mime, size, false);
        }

        // Path B Retry (just in case flow falls here unexpectedly)
        var handleRetry = await TryPathB_PhasedUploadAsync(client, c.WabaId, fileTypeParam, content, fileName, mime, size, ct);
        if (!string.IsNullOrWhiteSpace(handleRetry))
        {
            var resolvedId = await ResolveMediaIdAsync(client, handleRetry!, null, ct);
            return new HeaderUploadResult(resolvedId, mime, size, false);
        }

        throw new InvalidOperationException("Meta upload did not return an asset handle. Check app permissions and the response logs.");
    }

    // ‚îÄ‚îÄ Path A: WABA uploads session (single-shot Content-Range) ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ
    private async Task<string?> TryPathA_UploadsEndpointAsync(
        HttpClient client,
        string wabaId,
        string fileTypeParam,
        Stream content,
        string fileName,
        string mime,
        long size,
        CancellationToken ct)
    {
        // INIT
        // Params must be in URL query string for app/uploads AND Body to be safe.
        // We ensure messaging_product=whatsapp is present in both.

        var safeFileName = (fileName ?? "upload.bin").Replace(" ", "_");
        // Body (FormUrlEncoded)
        var initPayload = new Dictionary<string, string>
        {
            ["file_name"] = safeFileName,
            ["file_length"] = size.ToString(),
            ["file_type"] = fileTypeParam,
            ["messaging_product"] = "whatsapp"
        };

        // Query String (Extra safety for scoping)
        // Note: app/uploads sometimes ignores body for init, so query string is crucial.
        var initUrl = $"{client.BaseAddress}app/uploads?messaging_product=whatsapp";

        using var initResp = await client.PostAsync(initUrl, new FormUrlEncodedContent(initPayload), ct);
        var initText = await initResp.Content.ReadAsStringAsync(ct);

        if (!initResp.IsSuccessStatusCode)
        {
             _logger.LogWarning("Meta Upload Path A (Init) Failed. Status: {Status}, Body: {Body}", initResp.StatusCode, initText);
             return null;
        }

        var initJson = SafeParse(initText);
        var uploadId = initJson.TryGetValue("id", out var idObj) ? idObj?.ToString() : null;
        if (string.IsNullOrWhiteSpace(uploadId)) return null;

        // TRANSFER (single range)
        // Use absolute URI to prevent "scheme not supported" error on "upload:xxx"
        using (var req = new HttpRequestMessage(HttpMethod.Post, $"{client.BaseAddress}{uploadId}?messaging_product=whatsapp"))
        {
            req.Content = new StreamContent(content);
            req.Content.Headers.ContentType = new MediaTypeHeaderValue("application/octet-stream");
            req.Content.Headers.ContentLength = size;
            req.Headers.TryAddWithoutValidation("Content-Range", $"bytes 0-{size - 1}/{size}");

            using var upResp = await client.SendAsync(req, HttpCompletionOption.ResponseHeadersRead, ct);
            var upText = await upResp.Content.ReadAsStringAsync(ct);
            if (!upResp.IsSuccessStatusCode)
            {
                 _logger.LogWarning("Meta Upload Path A (Transfer) Failed. Status: {Status}, Body: {Body}", upResp.StatusCode, upText);
                 return null;
            }
        }

        // FINISH
        var finPayload = new Dictionary<string, string> { ["finish"] = "true" };
        var finUrl = $"{client.BaseAddress}{uploadId}?messaging_product=whatsapp";
        using var finResp = await client.PostAsync(finUrl, new FormUrlEncodedContent(finPayload), ct);
        var finText = await finResp.Content.ReadAsStringAsync(ct);

        if (!finResp.IsSuccessStatusCode)
        {
             _logger.LogWarning("Meta Upload Path A (Finish) Failed. Status: {Status}, Body: {Body}", finResp.StatusCode, finText);
             return null;
        }

        // Log success body to debug handle extraction
        _logger.LogInformation("Meta Upload Path A (Finish) Success. Body: {Body}", finText);

        var finJson = SafeParse(finText);
        if (TryExtractHandle(finJson, out var handle))
            return handle;

        return null;
    }

    // ‚îÄ‚îÄ Path B: classic phased upload (start/transfer/finish) ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ
    private async Task<string?> TryPathB_PhasedUploadAsync(
        HttpClient client,
        string wabaId,
        string fileTypeParam,
        Stream content,
        string fileName,
        string mime,
        long size,
        CancellationToken ct)
    {
        // START
        var startPayload = new Dictionary<string, string>
        {
            ["upload_phase"] = "start",
            ["file_type"] = fileTypeParam,
            ["file_length"] = size.ToString(),
            ["messaging_product"] = "whatsapp"
        };
        using var startResp = await client.PostAsync("app/uploads", new FormUrlEncodedContent(startPayload), ct);
        var startText = await startResp.Content.ReadAsStringAsync(ct);

        if (!startResp.IsSuccessStatusCode)
        {
             _logger.LogWarning("Meta Upload Path B (Start) Failed. Status: {Status}, Body: {Body}", startResp.StatusCode, startText);
             return null;
        }

        var startJson = SafeParse(startText);
        var sessionId = startJson.TryGetValue("id", out var idObj) ? idObj?.ToString() : null;
        if (string.IsNullOrWhiteSpace(sessionId)) return null;

        // TRANSFER
        var transferSep = sessionId.Contains('?') ? "&" : "?";
        using (var req = new HttpRequestMessage(HttpMethod.Post, $"{client.BaseAddress}{sessionId}{transferSep}upload_phase=transfer&messaging_product=whatsapp"))
        {
            req.Content = new StreamContent(content);
            req.Content.Headers.ContentType = new MediaTypeHeaderValue("application/octet-stream");
            req.Content.Headers.ContentLength = size;
            req.Headers.TryAddWithoutValidation("Content-Range", $"bytes 0-{size - 1}/{size}");

            using var transferResp = await client.SendAsync(req, HttpCompletionOption.ResponseHeadersRead, ct);
            var transferText = await transferResp.Content.ReadAsStringAsync(ct);
            if (!transferResp.IsSuccessStatusCode)
            {
                 _logger.LogWarning("Meta Upload Path B (Transfer) Failed. Status: {Status}, Body: {Body}", transferResp.StatusCode, transferText);
                 return null;
            }
        }

        // FINISH
        var finishSep = sessionId.Contains('?') ? "&" : "?";
        var finishUrl = $"{client.BaseAddress}{sessionId}{finishSep}upload_phase=finish&messaging_product=whatsapp";
        using (var req = new HttpRequestMessage(HttpMethod.Post, finishUrl))
        {
            req.Content = new FormUrlEncodedContent(new Dictionary<string, string> { ["confirm"] = "true" });
            using var finishResp = await client.SendAsync(req, ct);
            var finishText = await finishResp.Content.ReadAsStringAsync(ct);

            if (!finishResp.IsSuccessStatusCode)
            {
                 _logger.LogWarning("Meta Upload Path B (Finish) Failed. Status: {Status}, Body: {Body}", finishResp.StatusCode, finishText);
                 return null;
            }

            // Log success body to debug handle extraction
            _logger.LogInformation("Meta Upload Path B (Finish) Success. Body: {Body}", finishText);

            var finJson = SafeParse(finishText);
            if (TryExtractHandle(finJson, out var handle))
                return handle;
        }

        return null;
    }

    // ‚îÄ‚îÄ JSON helpers ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ
    private static Dictionary<string, object?> SafeParse(string json)
    {
        try
        {
            var root = JsonSerializer.Deserialize<Dictionary<string, object?>>(json);
            return root ?? new Dictionary<string, object?>();
        }
        catch
        {
            return new Dictionary<string, object?>();
        }
    }

    private static bool TryExtractHandle(Dictionary<string, object?> json, out string? handle)
    {
        handle = null;
        if (json == null) return false;

        // Keys to check in order of preference
        // Added "id" because some Meta regions return the asset handle in the "id" field during Finish.
        var keys = new[] { "h", "handle", "asset_handle", "id" };

        foreach (var key in keys)
        {
            if (json.TryGetValue(key, out var val) && val is not null)
            {
                var s = val.ToString();
                // If it's a JsonElement, GetString() is safer/cleaner than ToString()
                if (val is JsonElement el)
                {
                    if (el.ValueKind == JsonValueKind.String)
                        s = el.GetString();
                    else 
                        s = el.ToString(); 
                }

                if (!string.IsNullOrWhiteSpace(s))
                {
                    handle = s;
                    return true;
                }
            }
        }

        // nested { "result": ... }
        if (json.TryGetValue("result", out var resObj) && resObj is JsonElement resEl && resEl.ValueKind == JsonValueKind.Object)
        {
            foreach (var key in keys)
            {
                if (resEl.TryGetProperty(key, out var prop))
                {
                     var s = prop.ValueKind == JsonValueKind.String ? prop.GetString() : prop.ToString();
                     if (!string.IsNullOrWhiteSpace(s))
                     {
                         handle = s;
                         return true;
                     }
                }
            }
        }

        return false;
    }

    // ‚îÄ‚îÄ Path C: Classic Media Endpoint (POST /<targetId>/media) ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ
    private async Task<string?> TryPathC_ClassicMediaEndpointAsync(
        HttpClient client,
        string targetId,
        Stream content,
        string fileName,
        string mime,
        long size,
        CancellationToken ct)
    {
        // 25MB limit hard check (though server might reject earlier)
        // If > 25MB, skip immediately to fallback to resumable
        if (size > 25 * 1024 * 1024) return null;

        using var form = new MultipartFormDataContent(); 

        // Correct field: messaging_product="whatsapp"
        // We need to be careful with MultipartFormDataContent.Add(content, name, fileName)

        var mp = new MultipartFormDataContent();
        mp.Add(new StringContent("whatsapp"), "messaging_product");

        var fileContent = new StreamContent(content);
        fileContent.Headers.ContentType = new MediaTypeHeaderValue(mime);
        mp.Add(fileContent, "file", fileName);

        using var resp = await client.PostAsync($"{targetId}/media", mp, ct);
        var text = await resp.Content.ReadAsStringAsync(ct);

        if (!resp.IsSuccessStatusCode)
        {
             _logger.LogWarning("Meta Upload Path C (Classic) Failed. Status: {Status}, Body: {Body}", resp.StatusCode, text);
             return null;
        }

        _logger.LogInformation("Meta Upload Path C (Classic) Success. Body: {Body}", text);
        var json = SafeParse(text);

        // Classic endpoint returns { "id": "1234..." }
        if (json.TryGetValue("id", out var val) && val is not null)
        {
             return val.ToString();
        }

        return null;
    }

    // ‚îÄ‚îÄ Resolve Handle (GET handle -> ID) ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ
    // ‚îÄ‚îÄ Resolve Media ID (Handle or Session -> ID) ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ
    private async Task<string> ResolveMediaIdAsync(HttpClient client, string handle, string? sessionId, CancellationToken ct)
    {
        // Strategy 1: GET {handle}
        try
        {
            // Handle might contain chars that need encoding for URL path?
            // "4:..." usually safe, but let's be sure.
            // Actually, HttpClient doesn't like encoded colons in path sometimes.
            // But 4:: is weird. Let's try direct first.

            var reqUrl = handle;
            // If handle contains crazy chars, Uri creation might fail.
            // Let's try just GET.

            var resp = await client.GetAsync(reqUrl, ct);
            if (resp.IsSuccessStatusCode)
            {
                var jsonText = await resp.Content.ReadAsStringAsync(ct);
                _logger.LogInformation("Resolve(Handle) Success: {Handle} -> {Body}", handle, jsonText);
                var json = SafeParse(jsonText);
                if (json.TryGetValue("id", out var val) && val is not null) return val.ToString()!;
            }
            else
            {
                 _logger.LogWarning("Resolve(Handle) Failed: {Handle} Status: {Status}", handle, resp.StatusCode);
            }
        }
        catch (Exception ex)
        {
            _logger.LogError(ex, "Resolve(Handle) Exception: {Handle}", handle);
        }

        // Strategy 2: If sessionId provided, GET {sessionId}?fields=id,h
        if (!string.IsNullOrWhiteSpace(sessionId))
        {
            try
            {
                var resp = await client.GetAsync($"{sessionId}?fields=id,handle,status", ct);
                if (resp.IsSuccessStatusCode)
                {
                    var jsonText = await resp.Content.ReadAsStringAsync(ct);
                    _logger.LogInformation("Resolve(Session) Success: {SessionId} -> {Body}", sessionId, jsonText);
                    var json = SafeParse(jsonText);
                    if (json.TryGetValue("id", out var val) && val is not null) return val.ToString()!;
                }
                else
                {
                     _logger.LogWarning("Resolve(Session) Failed: {SessionId} Status: {Status}", sessionId, resp.StatusCode);
                }
            }
             catch (Exception ex)
            {
                _logger.LogError(ex, "Resolve(Session) Exception: {SessionId}", sessionId);
            }
        }

        return handle;
    }

    // ‚îÄ‚îÄ MIME guessing ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ
    private static string GuessMime(string? fileName, HeaderMediaType mediaType)
    {
        var ext = Path.GetExtension(fileName ?? string.Empty).ToLowerInvariant();

        if (mediaType == HeaderMediaType.IMAGE)
            return ext switch
            {
                ".jpg" or ".jpeg" => "image/jpeg",
                ".png" => "image/png",
                ".webp" => "image/webp",
                _ => "image/jpeg"
            };

        if (mediaType == HeaderMediaType.VIDEO)
            return ext switch
            {
                ".3gp" or ".3gpp" => "video/3gpp",
                _ => "video/mp4"
            };

        if (mediaType == HeaderMediaType.DOCUMENT)
            return ext switch
            {
                ".pdf" => "application/pdf",
                _ => "application/pdf"
            };

        return "application/octet-stream";
    }
    // ‚îÄ‚îÄ Stream Wrapper to prevent disposal ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ
    private class NonDisposableStream : Stream
    {
        private readonly Stream _inner;
        public NonDisposableStream(Stream inner) => _inner = inner;
        public override bool CanRead => _inner.CanRead;
        public override bool CanSeek => _inner.CanSeek;
        public override bool CanWrite => _inner.CanWrite;
        public override long Length => _inner.Length;
        public override long Position { get => _inner.Position; set => _inner.Position = value; }
        public override void Flush() => _inner.Flush();
        public override int Read(byte[] buffer, int offset, int count) => _inner.Read(buffer, offset, count);
        public override long Seek(long offset, SeekOrigin origin) => _inner.Seek(offset, origin);
        public override void SetLength(long value) => _inner.SetLength(value);
        public override void Write(byte[] buffer, int offset, int count) => _inner.Write(buffer, offset, count);

        // Key: Do NOT dispose the inner stream
        protected override void Dispose(bool disposing) { /* no-op */ }
    }
}

using Microsoft.EntityFrameworkCore;
using System.Text.RegularExpressions;
using xbytechat.api.Features.TemplateModule.Abstractions;
using xbytechat.api.Features.TemplateModule.Models;
using xbytechat.api.Features.TemplateModule.Services;
using xbytechat.api.Features.WhatsAppSettings.Models;

namespace xbytechat.api.Features.TemplateModule.Services;

public sealed class TemplateDraftLifecycleService : ITemplateDraftLifecycleService
{
    private readonly AppDbContext _db;
    private readonly IMetaTemplateClient _meta;
    private static readonly Regex MetaNameRx = new("^[a-z][a-z0-9_]*$", RegexOptions.Compiled);

    public TemplateDraftLifecycleService(AppDbContext db, IMetaTemplateClient meta)
    {
        _db = db;
        _meta = meta;
    }

    public async Task<TemplateDraft> DuplicateDraftAsync(Guid businessId, Guid draftId, string newKey, CancellationToken ct = default)
    {
        var src = await _db.TemplateDrafts.AsNoTracking()
            .FirstOrDefaultAsync(x => x.Id == draftId && x.BusinessId == businessId, ct);
        if (src is null) throw new KeyNotFoundException("Draft not found.");

        newKey = (newKey ?? string.Empty).Trim();
        if (string.IsNullOrWhiteSpace(newKey))
            throw new ArgumentException("Template name is required.", nameof(newKey));

        if (newKey.Length > 25 || !MetaNameRx.IsMatch(newKey))
            throw new ArgumentException(
                "Invalid template name. Use lowercase letters/numbers/underscores, start with a letter, max 25 characters.",
                nameof(newKey));

        var exists = await _db.TemplateDrafts.AnyAsync(
            x => x.BusinessId == businessId && x.Key == newKey,
            ct);

        if (exists)
            throw new InvalidOperationException("Template name already exists. Please choose a different name.");

        var now = DateTime.UtcNow;
        var dup = new TemplateDraft
        {
            Id = Guid.NewGuid(),
            BusinessId = businessId,
            Key = newKey,
            Category = src.Category,
            DefaultLanguage = src.DefaultLanguage,
            CreatedAt = now,
            UpdatedAt = now
        };

        _db.TemplateDrafts.Add(dup);

        var variants = await _db.TemplateDraftVariants
            .AsNoTracking()
            .Where(v => v.TemplateDraftId == src.Id)
            .ToListAsync(ct);

        foreach (var v in variants)
        {
            _db.TemplateDraftVariants.Add(new TemplateDraftVariant
            {
                Id = Guid.NewGuid(),
                TemplateDraftId = dup.Id,
                Language = v.Language,
                HeaderType = v.HeaderType,
                HeaderText = v.HeaderText,
                HeaderMediaLocalUrl = v.HeaderMediaLocalUrl,
                BodyText = v.BodyText,
                FooterText = v.FooterText,
                ButtonsJson = v.ButtonsJson,
                ExampleParamsJson = v.ExampleParamsJson,
                IsReadyForSubmission = v.IsReadyForSubmission,
                ValidationErrorsJson = v.ValidationErrorsJson
            });
        }

        await _db.SaveChangesAsync(ct);
        return dup;
    }

    public async Task<bool> DeleteDraftAsync(Guid businessId, Guid draftId, CancellationToken ct = default)
    {
        // 1) Find the draft scoped to the tenant
        var draft = await _db.TemplateDrafts
            .FirstOrDefaultAsync(x => x.Id == draftId && x.BusinessId == businessId, ct);

        if (draft is null) return false;

        // 2) Fetch variants to delete from Meta
        var variants = await _db.TemplateDraftVariants
            .Where(v => v.TemplateDraftId == draft.Id)
            .ToListAsync(ct);

        foreach (var v in variants)
        {
            // Try to delete from Meta (best effort)
            try
            {
                await _meta.DeleteTemplateAsync(businessId, draft.Key, v.Language, ct);
            }
            catch (Exception ex)
            {
                Console.WriteLine($"[DeleteDraft] Meta delete warning for {draft.Key}/{v.Language}: {ex.Message}");
            }

            // Also Soft-delete locally in WhatsAppTemplates (synced table)
            var syncRows = await _db.WhatsAppTemplates
                .Where(t => t.BusinessId == businessId && t.Name == draft.Key && t.LanguageCode == v.Language)
                .ToListAsync(ct);

            if (syncRows.Count > 0)
            {
                var now = DateTime.UtcNow;
                foreach (var t in syncRows)
                {
                    t.IsActive = false;
                    t.Status = "DELETED";
                    t.UpdatedAt = now;
                    t.LastSyncedAt = now;
                }
            }
        }

        // 3) Hard-delete variants explicitly
        try
        {
            await _db.TemplateDraftVariants
                .Where(v => v.TemplateDraftId == draft.Id)
                .ExecuteDeleteAsync(ct);
        }
        catch (NotSupportedException)
        {
            if (variants.Count > 0)
                _db.TemplateDraftVariants.RemoveRange(variants);
        }

        // 4) Delete the parent draft
        _db.TemplateDrafts.Remove(draft);

        // 5) Commit
        await _db.SaveChangesAsync(ct);
        return true;
    }

    public async Task<bool> DeleteApprovedTemplateAsync(Guid businessId, string name, string language, CancellationToken ct = default)
    {
        if (string.IsNullOrWhiteSpace(name) || string.IsNullOrWhiteSpace(language))
            throw new ArgumentException("Name and language are required.");

        // 1) Ask Meta to delete
        var okMeta = await _meta.DeleteTemplateAsync(businessId, name, language, ct);

        // 2) Soft-delete locally even if Meta already deleted (idempotent UX)
        var rows = await _db.WhatsAppTemplates
            .Where(t => t.BusinessId == businessId && t.Name == name && t.LanguageCode == language)
            .ToListAsync(ct);

        if (rows.Count > 0)
        {
            var now = DateTime.UtcNow;
            foreach (var t in rows)
            {
                t.IsActive = false;
                t.Status = "DELETED"; // keep a conventional marker
                t.UpdatedAt = now;
                t.LastSyncedAt = now;
            }
            await _db.SaveChangesAsync(ct);
        }

        return okMeta;
    }
}

using System.Text.Json;
using System.Text.RegularExpressions;
using Microsoft.EntityFrameworkCore;
using xbytechat.api.Features.TemplateModule.Abstractions;
using xbytechat.api.Features.TemplateModule.DTOs;
using xbytechat.api.Features.TemplateModule.Models;
using xbytechat.api.Features.TemplateModule.Payload;
using xbytechat.api.Features.TemplateModule.Validators;

namespace xbytechat.api.Features.TemplateModule.Services;

public sealed class TemplateDraftService : ITemplateDraftService
{
    private readonly AppDbContext _db;
    private static readonly JsonSerializerOptions JsonOpts = new(JsonSerializerDefaults.Web);
    private static readonly Regex KeyAllowed = new("^[a-z0-9_]+$", RegexOptions.Compiled);
    private static readonly HashSet<string> AllowedCategories =
        new(StringComparer.OrdinalIgnoreCase) { "UTILITY", "MARKETING", "AUTHENTICATION" };

    public TemplateDraftService(AppDbContext db)
    {
        _db = db;
    }

    public async Task<TemplateDraft> CreateDraftAsync(Guid businessId, TemplateDraftCreateDto dto, CancellationToken ct = default)
    {
        if (dto is null) throw new InvalidOperationException("Body required.");
        if (string.IsNullOrWhiteSpace(dto.Key)) throw new InvalidOperationException("Key is required.");
        var key = dto.Key.Trim().ToLowerInvariant();
        if (key.Length > 64) throw new InvalidOperationException("Key is too long (max 64 chars).");
        if (!KeyAllowed.IsMatch(key))
            throw new InvalidOperationException("Key must contain only lowercase letters, numbers, and underscores.");

        var category = (dto.Category ?? "UTILITY").Trim().ToUpperInvariant();
        if (!AllowedCategories.Contains(category))
            throw new InvalidOperationException("Category must be UTILITY, MARKETING, or AUTHENTICATION.");

        var exists = await _db.TemplateDrafts
            .AnyAsync(x => x.BusinessId == businessId && x.Key == key, ct);
        if (exists)
            throw new InvalidOperationException("A draft with the same Key already exists for this business.");

        var draft = new TemplateDraft
        {
            Id = Guid.NewGuid(),
            BusinessId = businessId,
            Key = key,
            Category = category,
            DefaultLanguage = string.IsNullOrWhiteSpace(dto.DefaultLanguage) ? "en_US" : dto.DefaultLanguage.Trim(),
            CreatedAt = DateTime.UtcNow,
            UpdatedAt = DateTime.UtcNow
        };

        _db.TemplateDrafts.Add(draft);
        await _db.SaveChangesAsync(ct);
        return draft;
    }

    public async Task<TemplateDraft> UpdateDraftAsync(Guid businessId, Guid draftId, TemplateDraftUpdateDto dto, CancellationToken ct = default)
    {
        if (draftId == Guid.Empty) throw new InvalidOperationException("Invalid draftId.");
        if (dto is null) throw new InvalidOperationException("Body required.");

        var draft = await _db.TemplateDrafts
            .FirstOrDefaultAsync(x => x.Id == draftId && x.BusinessId == businessId, ct);
        if (draft is null) throw new KeyNotFoundException("Draft not found for this business.");

        if (dto.Key is not null)
        {
            var key = dto.Key.Trim().ToLowerInvariant();
            if (string.IsNullOrWhiteSpace(key))
                throw new InvalidOperationException("Key is required.");
            if (key.Length > 64)
                throw new InvalidOperationException("Key is too long (max 64 chars).");
            if (!KeyAllowed.IsMatch(key))
                throw new InvalidOperationException("Key must contain only lowercase letters, numbers, and underscores.");

            if (!string.Equals(draft.Key, key, StringComparison.Ordinal))
            {
                var exists = await _db.TemplateDrafts
                    .AnyAsync(x => x.BusinessId == businessId && x.Key == key && x.Id != draft.Id, ct);
                if (exists)
                    throw new InvalidOperationException("A draft with the same Key already exists for this business.");
                draft.Key = key;
            }
        }

        if (dto.Category is not null)
        {
            var category = dto.Category.Trim().ToUpperInvariant();
            if (!AllowedCategories.Contains(category))
                throw new InvalidOperationException("Category must be UTILITY, MARKETING, or AUTHENTICATION.");
            draft.Category = category;
        }

        if (dto.DefaultLanguage is not null)
        {
            var lang = dto.DefaultLanguage.Trim();
            if (string.IsNullOrWhiteSpace(lang))
                throw new InvalidOperationException("DefaultLanguage is required.");
            draft.DefaultLanguage = lang;
        }

        draft.UpdatedAt = DateTime.UtcNow;
        await _db.SaveChangesAsync(ct);
        return draft;
    }

    public async Task<TemplateDraftVariant> UpsertVariantAsync(Guid businessId, Guid draftId, TemplateDraftVariantUpsertDto dto, CancellationToken ct = default)
    {
        var draft = await _db.TemplateDrafts
            .FirstOrDefaultAsync(x => x.Id == draftId && x.BusinessId == businessId, ct);

        if (draft is null)
            throw new KeyNotFoundException("Draft not found for this business.");

        var validation = VariantValidator.Validate(dto);
        if (!validation.Ok)
            throw new InvalidOperationException(string.Join(" | ", validation.Errors));

        var lang = dto.Language.Trim();

        var variant = await _db.TemplateDraftVariants
            .FirstOrDefaultAsync(v => v.TemplateDraftId == draft.Id && v.Language == lang, ct);

        var buttonsJson = JsonSerializer.Serialize(dto.Buttons ?? new List<ButtonDto>(), JsonOpts);
        var examplesJson = JsonSerializer.Serialize(dto.Examples ?? new Dictionary<string, string>(), JsonOpts);

        if (variant is null)
        {
            variant = new TemplateDraftVariant
            {
                Id = Guid.NewGuid(),
                TemplateDraftId = draft.Id,
                Language = lang,
                BodyText = dto.BodyText,
                HeaderType = dto.HeaderType,
                HeaderText = dto.HeaderText,
                HeaderMediaLocalUrl = dto.HeaderMediaLocalUrl,
                FooterText = dto.FooterText,
                ButtonsJson = buttonsJson,
                ExampleParamsJson = examplesJson,
                IsReadyForSubmission = true,
                ValidationErrorsJson = null
            };
            _db.TemplateDraftVariants.Add(variant);
        }
        else
        {
            variant.BodyText = dto.BodyText;
            variant.HeaderType = dto.HeaderType;
            variant.HeaderText = dto.HeaderText;
            variant.HeaderMediaLocalUrl = dto.HeaderMediaLocalUrl;
            variant.FooterText = dto.FooterText;
            variant.ButtonsJson = buttonsJson;
            variant.ExampleParamsJson = examplesJson;
            variant.IsReadyForSubmission = true;
            variant.ValidationErrorsJson = null;
        }

        draft.UpdatedAt = DateTime.UtcNow;

        await _db.SaveChangesAsync(ct);
        return variant;
    }

    public async Task<bool> ValidateAsync(Guid businessId, Guid draftId, CancellationToken ct = default)
    {
        var draft = await _db.TemplateDrafts
            .FirstOrDefaultAsync(x => x.Id == draftId && x.BusinessId == businessId, ct);
        if (draft is null) return false;

        var variants = await _db.TemplateDraftVariants
            .Where(v => v.TemplateDraftId == draft.Id)
            .ToListAsync(ct);

        return variants.Any(v => v.IsReadyForSubmission);
    }

    public async Task<(bool ok, Dictionary<string, List<string>> errors)> ValidateAllAsync(Guid businessId, Guid draftId, CancellationToken ct = default)
    {
        var draft = await _db.TemplateDrafts
            .FirstOrDefaultAsync(x => x.Id == draftId && x.BusinessId == businessId, ct);
        if (draft is null) return (false, new() { { "global", new() { "Draft not found." } } });

        var variants = await _db.TemplateDraftVariants
            .Where(v => v.TemplateDraftId == draft.Id)
            .ToListAsync(ct);

        if (variants.Count == 0)
            return (false, new() { { "global", new() { "No language variants found." } } });

        // Map DB rows to validator view
        var views = variants.Select(v => new MultiLanguageValidator.VariantView
        {
            Language = v.Language,
            BodyText = v.BodyText,
            HeaderType = v.HeaderType,
            HeaderText = v.HeaderText,
            HeaderMediaLocalUrl = v.HeaderMediaLocalUrl,
            FooterText = v.FooterText,
            Buttons = SafeDeserialize<List<ButtonDto>>(v.ButtonsJson) ?? new(),
            Examples = SafeDeserialize<Dictionary<string, string>>(v.ExampleParamsJson) ?? new()
        }).ToList();

        var (ok, errors) = MultiLanguageValidator.Validate(views);

        // Persist per-variant readiness & last validation errors (optional but helpful)
        foreach (var v in variants)
        {
            v.IsReadyForSubmission = !errors.ContainsKey(v.Language) || errors[v.Language].Count == 0;
            v.ValidationErrorsJson = errors.ContainsKey(v.Language)
                ? JsonSerializer.Serialize(errors[v.Language], JsonOpts)
                : null;
        }
        await _db.SaveChangesAsync(ct);

        return (ok, errors);
    }
    public Task<TemplateDraft?> GetDraftAsync(Guid businessId, Guid draftId, CancellationToken ct = default)
        => _db.TemplateDrafts.AsNoTracking()
            .FirstOrDefaultAsync(x => x.Id == draftId && x.BusinessId == businessId, ct);

    public async Task<IReadOnlyList<DraftListItemDto>> ListDraftsAsync(Guid businessId, CancellationToken ct = default)
    {
        // Exclude drafts that have been submitted (SubmittedAt is not null)
        // Also exclude drafts that match synced templates (belt-and-suspenders approach)
        var submittedTemplateNames = await _db.WhatsAppTemplates
            .Where(t => t.BusinessId == businessId)
            .Select(t => t.Name.ToLower())
            .Distinct()
            .ToListAsync(ct);

        // Join Drafts with their Default Variant to get preview content
        var query = from d in _db.TemplateDrafts.AsNoTracking()
                    where d.BusinessId == businessId 
                       && d.SubmittedAt == null 
                       && !submittedTemplateNames.Contains(d.Key)
                    let v = _db.TemplateDraftVariants.FirstOrDefault(x => x.TemplateDraftId == d.Id && x.Language == d.DefaultLanguage)
                    // üóëÔ∏è Filter out "empty" drafts (Untitled + No Content)
                    where !d.Key.StartsWith("untitled_") 
                          || (v != null && (!string.IsNullOrWhiteSpace(v.BodyText) || v.HeaderType != "NONE"))
                    orderby d.UpdatedAt descending
                    select new DraftListItemDto
                    {
                        Id = d.Id,
                        Key = d.Key,
                        Category = d.Category,
                        DefaultLanguage = d.DefaultLanguage,
                        UpdatedAt = d.UpdatedAt,
                        // Construct simulated components for frontend preview
                        Components = new List<ComponentItemDto>
                        {
                            new ComponentItemDto { Type = "HEADER", Format = v != null ? v.HeaderType : "NONE" },
                            new ComponentItemDto { Type = "BODY", Text = v != null ? v.BodyText : "" }
                        }
                    };

        return await query.ToListAsync(ct);
    }

    private static T? SafeDeserialize<T>(string? json)
    {
        if (string.IsNullOrWhiteSpace(json)) return default;
        try { return JsonSerializer.Deserialize<T>(json!, JsonOpts); } catch { return default; }
    }

    public async Task<bool> SetHeaderHandleAsync(
    Guid businessId,
    Guid draftId,
    string language,
    string mediaType,
    string assetHandle,
    CancellationToken ct = default)
    {
        var draft = await _db.TemplateDrafts
            .FirstOrDefaultAsync(x => x.Id == draftId && x.BusinessId == businessId, ct);
        if (draft is null) return false;

        var lang = language.Trim();
        var variant = await _db.TemplateDraftVariants
            .FirstOrDefaultAsync(v => v.TemplateDraftId == draft.Id && v.Language == lang, ct);

        if (variant is null)
        {
            // create the variant if it doesn't exist yet (handy UX)
            variant = new TemplateDraftVariant
            {
                Id = Guid.NewGuid(),
                TemplateDraftId = draft.Id,
                Language = lang,
                BodyText = string.Empty,
                HeaderType = mediaType.ToUpperInvariant(),
                HeaderText = null,
                HeaderMediaLocalUrl = $"handle:{assetHandle}",
                FooterText = null,
                ButtonsJson = "[]",
                ExampleParamsJson = "{}",
                IsReadyForSubmission = false,
                ValidationErrorsJson = null
            };
            _db.TemplateDraftVariants.Add(variant);
        }
        else
        {
            variant.HeaderType = mediaType.ToUpperInvariant(); // IMAGE|VIDEO|DOCUMENT
            variant.HeaderMediaLocalUrl = $"handle:{assetHandle}";
            // don't force IsReadyForSubmission here; Validate/Submit will compute it
        }

        draft.UpdatedAt = DateTime.UtcNow;
        await _db.SaveChangesAsync(ct);
        return true;
    }
