using System.Diagnostics.Metrics;
using Microsoft.Extensions.Configuration;

namespace xbytechat.api.Infrastructure.Observability
{
    /// <summary>
    /// Central metrics facade with a hard OFF switch.
    /// When Observability:Metrics:Enabled=false, all calls no-op.
    /// </summary>
    public static class MetricsRegistry
    {
        private static bool _enabled;
        private static Meter? _meter;
        private static long _queueDepth;

        // Proxies let existing call sites stay unchanged.
        public static readonly CounterProxy MessagesSent = new();
        public static readonly CounterProxy MessagesFailed = new();
        public static readonly CounterProxy RateLimited429s = new();
        public static readonly HistProxy SendLatencyMs = new();

        /// <summary>Call once at startup from Program.cs.</summary>
        public static void Configure(IConfiguration cfg)
        {
            _enabled = cfg.GetValue<bool>("Observability:Metrics:Enabled", false);

            if (!_enabled)
            {
                _meter = null; // proxies remain unbound => no-ops
                return;
            }

            _meter = new Meter("xbytechat.api", "1.0.0");

            // Bind proxies to actual instruments
            MessagesSent.Bind(_meter.CreateCounter<long>("campaign_messages_sent", unit: "msg",
                description: "Accepted by provider"));
            MessagesFailed.Bind(_meter.CreateCounter<long>("campaign_messages_failed", unit: "msg",
                description: "Failed sends"));
            RateLimited429s.Bind(_meter.CreateCounter<long>("campaign_http_429", unit: "hit",
                description: "HTTP 429 responses"));
            SendLatencyMs.Bind(_meter.CreateHistogram<double>("campaign_send_latency_ms", unit: "ms",
                description: "Provider send latency"));

            _meter.CreateObservableGauge("campaign_queue_depth",
                () => new Measurement<long>(_queueDepth),
                unit: "jobs",
                description: "Outbound campaign jobs waiting in this process");
        }

        public static void ReportQueueDepth(long depth)
        {
            if (_enabled) _queueDepth = depth;
        }

        // ---------- Proxies (no-ops when not bound / disabled) ----------

        public sealed class CounterProxy
        {
            private Counter<long>? _inner;
            internal void Bind(Counter<long> inner) => _inner = inner;
            public void Add(long value) { if (_enabled) _inner?.Add(value); }
        }

        public sealed class HistProxy
        {
            private Histogram<double>? _inner;
            internal void Bind(Histogram<double> inner) => _inner = inner;
            public void Record(double value) { if (_enabled) _inner?.Record(value); }
        }
    }
}

using System.Collections.Concurrent;
using System.Threading.RateLimiting;

namespace xbytechat.api.Infrastructure.RateLimiting
{
    /// <summary>
    /// Token-bucket limiter per senderKey (Provider|PhoneNumberId).
    /// Default: 10 req/s with burst 10. You can tune dynamically.
    /// </summary>
    public interface IPhoneNumberRateLimiter
    {
        ValueTask<RateLimitLease> AcquireAsync(string senderKey, CancellationToken ct);
        void UpdateLimits(string senderKey, int permitsPerSecond, int burst);
    }

    public sealed class PhoneNumberRateLimiter : IPhoneNumberRateLimiter
    {
        private readonly ConcurrentDictionary<string, TokenBucketRateLimiter> _buckets = new();

        public ValueTask<RateLimitLease> AcquireAsync(string senderKey, CancellationToken ct)
        {
            var limiter = _buckets.GetOrAdd(senderKey, _ => Create(permitsPerSecond: 10, burst: 10));
            return limiter.AcquireAsync(1, ct);
        }

        public void UpdateLimits(string senderKey, int permitsPerSecond, int burst)
        {
            _buckets.AddOrUpdate(senderKey,
                _ => Create(permitsPerSecond, burst),
                (_, __) => Create(permitsPerSecond, burst));
        }

        private static TokenBucketRateLimiter Create(int permitsPerSecond, int burst)
        {
            return new TokenBucketRateLimiter(new TokenBucketRateLimiterOptions
            {
                TokenLimit = Math.Max(1, burst),
                TokensPerPeriod = Math.Max(1, permitsPerSecond),
                ReplenishmentPeriod = TimeSpan.FromSeconds(1),   // ‚Üê correct property name
                QueueProcessingOrder = QueueProcessingOrder.OldestFirst,
                QueueLimit = 0                                    // no internal queue; your outbox already queues
            });
        }
    }
}

using System;
using System.Data;
using System.IO;
using System.Threading.Tasks;
using Microsoft.EntityFrameworkCore;

namespace xbytechat.api.Infrastructure.Schema
{
    public static class SchemaPatchRunner
    {
        /// <summary>
        /// Ensures a patch is applied exactly once. Creates the tracking table if needed.
        /// </summary>
        /// <param name="db">AppDbContext</param>
        /// <param name="patchId">A unique string, e.g. "20251003_campaign_cascade"</param>
        /// <param name="absoluteSqlPath">Absolute path to the .sql file</param>
        public static async Task EnsurePatchAsync(DbContext db, string patchId, string absoluteSqlPath)
        {
            if (!File.Exists(absoluteSqlPath))
                throw new FileNotFoundException($"Schema patch not found: {absoluteSqlPath}");

            var conn = db.Database.GetDbConnection();
            await conn.OpenAsync();

            await using var tx = await conn.BeginTransactionAsync();

            try
            {
                // 1) Create tracking table if not exists
                await using (var cmd = conn.CreateCommand())
                {
                    cmd.Transaction = tx;
                    cmd.CommandText = @"
CREATE TABLE IF NOT EXISTS __schema_patches (
    patch_id   text PRIMARY KEY,
    applied_at timestamp with time zone NOT NULL DEFAULT now()
);";
                    await cmd.ExecuteNonQueryAsync();
                }

                // 2) Check if already applied
                bool alreadyApplied;
                await using (var check = conn.CreateCommand())
                {
                    check.Transaction = tx;
                    check.CommandText = "SELECT 1 FROM __schema_patches WHERE patch_id = @p LIMIT 1;";
                    var p = check.CreateParameter();
                    p.ParameterName = "@p";
                    p.Value = patchId;
                    check.Parameters.Add(p);

                    await using var reader = await check.ExecuteReaderAsync();
                    alreadyApplied = await reader.ReadAsync();
                }

                if (!alreadyApplied)
                {
                    // 3) Run SQL file
                    var sql = await File.ReadAllTextAsync(absoluteSqlPath);

                    await using (var exec = conn.CreateCommand())
                    {
                        exec.Transaction = tx;
                        exec.CommandText = sql;
                        await exec.ExecuteNonQueryAsync();
                    }

                    // 4) Record success
                    await using (var ins = conn.CreateCommand())
                    {
                        ins.Transaction = tx;
                        ins.CommandText = "INSERT INTO __schema_patches (patch_id) VALUES (@p);";
                        var p = ins.CreateParameter();
                        p.ParameterName = "@p";
                        p.Value = patchId;
                        ins.Parameters.Add(p);
                        await ins.ExecuteNonQueryAsync();
                    }
                }

                await tx.CommitAsync();
            }
            catch
            {
                await tx.RollbackAsync();
                throw;
            }
            finally
            {
                await conn.CloseAsync();
            }
        }
    }
}

using System.Security.Claims;

namespace xbytechat.api.Infrastructure.Security
{
    public static class ClaimsPrincipalExtensions
    {
        // Adjust these claim types if your project uses different names
        private const string RoleClaimType = ClaimTypes.Role;          // typical
        private const string AltRoleClaimType = "role";                // common JWT
        private const string BusinessIdClaimType = "BusinessId";       // your system likely has this

        public static string? GetRole(this ClaimsPrincipal user)
        {
            return user.FindFirstValue(RoleClaimType)
                   ?? user.FindFirstValue(AltRoleClaimType);
        }

        public static Guid? GetBusinessId(this ClaimsPrincipal user)
        {
            var raw = user.FindFirstValue(BusinessIdClaimType);
            return Guid.TryParse(raw, out var id) ? id : null;
        }

        public static bool IsAdminLike(this ClaimsPrincipal user)
        {
            var role = (user.GetRole() ?? "").Trim();
            return role.Equals("Admin", StringComparison.OrdinalIgnoreCase)
                || role.Equals("SuperAdmin", StringComparison.OrdinalIgnoreCase);
        }

        public static bool IsBusinessOwnerLike(this ClaimsPrincipal user)
        {
            var role = (user.GetRole() ?? "").Trim();

            // Keep this flexible for MVP; tighten later once your role codes are finalized
            return role.Equals("Owner", StringComparison.OrdinalIgnoreCase)
                || role.Equals("BusinessOwner", StringComparison.OrdinalIgnoreCase)
                || role.Equals("Business_Admin", StringComparison.OrdinalIgnoreCase)
                || role.Equals("BusinessAdmin", StringComparison.OrdinalIgnoreCase);
        }
    }
}

